}

.pet h2 {
  white-space: nowrap;
  font-weight: normal;
  font-size: 20px;
  margin: 0;
}

header a {
  color: #fff;
  font-size: 70px;
  text-decoration: none;
  font-weight: bold;
  display: block;
}

header p {
  color: #fff;
  font-size: 20px;
