generated.css:
* {
  box-sizing: border-box;
  color: #333;
}

body {
  background-color: #9999ff;
  margin: 0;
  font-family: "Optima", Verdana, Trebuchet, sans-serif;
}

.search,
.details,
.search-params {
  background-color: #f2f2f2;
  width: 95%;
  margin: 0 auto;
  padding: 15px;
  border-radius: 5px;
}

.pet {
  width: 100%;
  height: 100px;
  display: block;
  overflow: hidden;
  margin: 15px 0;
}

.pet img {
  width: 100px;
  min-height: 100px;
}

.info {
  float: left;
  height: 100px;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
}

.image-container {
  clip-path: circle(50% at 50% 50%);
  width: 100px;
  height: 100px;
  float: left;
  margin-right: 30px;
  margin-left: 15px;
}

.pet h1 {
  white-space: nowrap;
  font-weight: normal;
  font-size: 30px;
  width: 100%;
  overflow: hidden;
  margin: 0;
}

.pet h2 {
  white-space: nowrap;
  font-weight: normal;
  font-size: 20px;
  margin: 0;
}

header a {
  color: #fff;
  font-size: 60px;
  text-decoration: none;
  font-weight: bold;
  display: block;
}

header p {
  color: #fff;
  font-size: 20px;
  text-decoration: none;
  display: block;
}

header {
  display: flex;
  align-content: center;
  justify-content: space-between;
  margin-bottom: 20px;
  padding: 20px;
}

.petprofile p {
  line-height: 2;
}

.petprofile h1,
.petprofile h2 {
  text-align: center;
}

.carousel {
  display: flex;
  justify-content: space-around;
  align-items: center;
  height: 400px;
}

.carousel > img {
  max-width: 45%;
  max-height: 500px;
}

.carousel-smaller {
  width: 50%;
}

.carousel-smaller > img {
  width: 150px;
  height: 150px;  
  border-radius: 50%;
  display: inline-block;
  margin: 20px;
  cursor: pointer;
  border: 2px solid #333;
}

.carousel-smaller > img.active {
  border-color: #9999ff;
  opacity: 0.6;
}
